import React, { lazy, useState, useRef, useEffect} from 'react'

import {
    CContainer ,
    CCallout,
    CButton,
    CCard,
    CCardTitle,
    CCardBody,
    CCol,
    CFormFloating,
    CFormInput,
    CFormLabel,
    CFormSelect,
    CRow,
    CForm,
    CToast,
    CToaster,
    CToastBody,
    CToastClose,
    CBadge,

  } from '@coreui/react'


const payRent = (props) => {

  const [email, setEmail] = useState('');
    var content = null;
  
    const getUser = () => {
        (
            async () => {
                const response = await fetch('http://localhost:8080/api/user', {
                    headers: {'Content-Type':'application/json'},
                    credentials:'include'
                });
                content = await response.json();
                setEmail(content.email);
                getData(content.email);
            }
        )();
    };

    const [data, setData] = useState(null);
    const [maintenanceData, setMaintenanceData] = useState(null);
    const [curr_dues, setCurrDues] = useState([]);
    const getData = (email) => {
      console.log(email); 

    fetch("http://localhost:8080/api/getDues", {
        method:"POST",
        headers: {'Content-Type':'application/json'},
        body: JSON.stringify({
          email
        })
    }).then(function (response) {
        console.log(response)
        return response.json();
    }).then(function(myJSON){
        setData(JSON.stringify(myJSON));
        setCurrDues(getTotalDues(JSON.stringify(myJSON)));
    })
    }
    
    useEffect(() => {
      getUser()
    },[])

    function getTotalDues(data)
    {
      var curr_dues = [];
      var data = JSON.parse(data)
      for (var i in data)
      {
        curr_dues.push(data[i].Amount)
      }
      // console.log(curr_dues);
      curr_dues = curr_dues.sort();
      return curr_dues
    }

    function clearDues()
    {
      alert(email);
    }
  return (
    <div>
      
      <CCard className='col-md-4 align-items-center text-center shadow'>
        <CCardTitle><h5 className='display-6'>Balance Due</h5></CCardTitle>
        <CCardBody>
          <CContainer>
            <CRow>
              <CCol sm="auto">
              <CCallout color="primary">
                  <h5>Rent 
                    <CBadge className='mx-1' color='primary'>
                      {curr_dues[1]}
                    </CBadge></h5>
                </CCallout>
            </CCol>
              <CCol sm="auto">
                <CCallout color="secondary">
                  <h5>Utility 
                    <CBadge className='mx-1' color='secondary'>
                      {curr_dues[0]}
                    </CBadge></h5>
                </CCallout>
              </CCol>
            </CRow>
            <CRow>
            <CCallout color="dark">
                  <h5>Total 
                    <CBadge className='mx-1' color='dark'>
                      {curr_dues[0]+curr_dues[1]}
                    </CBadge></h5>
                </CCallout>
            </CRow>
            <CRow>
              <CButton color='dark' onClick={clearDues()}>Pay Rent</CButton>
            </CRow>
          </CContainer>
        </CCardBody>
      </CCard>
        
    </div>
  )
}

export default payRent;
