import React, { lazy, useState, useRef, useEffect} from 'react'

import {
  CCallout ,
    CButton,
    CCard,
    CCardTitle,
    CCardBody,
    CCol,
    CFormFloating,
    CFormInput,
    CFormLabel,
    CFormSelect,
    CRow,
    CForm,
    CToast,
    CToaster,
    CToastBody,
    CToastClose,
    CBadge,

  } from '@coreui/react'


const payRent = (props) => {

  const [email, setEmail] = useState('');
    var content = null;
  
    const getUser = () => {
        (
            async () => {
                const response = await fetch('http://localhost:8080/api/user', {
                    headers: {'Content-Type':'application/json'},
                    credentials:'include'
                });
                content = await response.json();
                setEmail(content.email);
                getData(content.email);
            }
        )();
    };

    const [data, setData] = useState(null);
    const [maintenanceData, setMaintenanceData] = useState(null);

    const getData = (email) => {
      console.log(email); 

    fetch("http://localhost:8080/api/getDues", {
        method:"POST",
        headers: {'Content-Type':'application/json'},
        body: JSON.stringify({
          email
        })
    }).then(function (response) {
        console.log(response)
        return response.json();
    }).then(function(myJSON){
        setData(JSON.stringify(myJSON));
        getTotalDues(JSON.stringify(myJSON));
    })
    }
    
    useEffect(() => {
      getUser()
    },[])

    function getTotalDues(data)
    {
      var curr_dues = [];
      var data = JSON.parse(data)
      for (var i in data)
      {
        curr_dues.push(data[i].Amount)
      }
      console.log(curr_dues);
    }
  return (
    <div>
        <CCallout color="primary">
          Rent:
        </CCallout>
        <CCallout color="secondary">
          Utility:
        </CCallout>
    </div>
  )
}

export default payRent;
